BEGIN;


CREATE TABLE empenho (
    id_empenho integer PRIMARY KEY,
    id_unidade integer NOT NULL,
    id_favorecido integer NOT NULL,
    codigo_empenho character varying(50) NOT NULL,
    data_emissao date NOT NULL,
    observacao character varying(500),
    processo character varying(100) NOT NULL,
    valor_empenho numeric NOT NULL,
    FOREIGN KEY (id_unidade) REFERENCES unidade(id_unidade),
    FOREIGN KEY (id_favorecido) REFERENCES favorecido(id_favorecido)
);

CREATE TABLE favorecido (
    id_favorecido integer PRIMARY KEY,
    nome_fornecedor character varying(100) NOT NULL,
    uf character varying(2) NOT NULL
);

CREATE TABLE item_empenho (
    id_item_empenho integer PRIMARY KEY,
    id_empenho integer NOT NULL,
    descricao character varying(1000) NOT NULL,
    quantidade integer NOT NULL,
    valor_total_item numeric NOT NULL,
    FOREIGN KEY (id_empenho) REFERENCES empenho(id_empenho)
);

CREATE TABLE orgao (
    id_orgao integer PRIMARY KEY,
    codigo_orgao integer NOT NULL,
    nome_orgao character varying(100) NOT NULL
);

CREATE TABLE unidade (
    id_unidade integer PRIMARY KEY,
    id_orgao integer NOT NULL,
    codigo_unidade integer NOT NULL,
    nome_unidade character varying(100) NOT NULL,
    FOREIGN KEY (id_orgao) REFERENCES orgao(id_orgao)
);






END;

